import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "./Detalle.css"

import ActorList from "./ActorList";
import MovieDetail from "./MovieDetail";
import secret_key from "../../secret";
import axios from "axios";

const Detalle = () => {
  const [movie, setMovie] = useState({});
  const [actores, setActores] = useState([]);

  const { id } = useParams();

  useEffect(() => {
    axios
      .get(`https://api.themoviedb.org/3/movie/${id}?api_key=${secret_key}`)
      //  .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setMovie({ ...data.data });
        getActorsByMovieId();
      });
    console.log(`Este es el id: ${id}`);
  }, []);

  const getActorsByMovieId = () => {
    fetch(
      `https://api.themoviedb.org/3/movie/${id}/credits?api_key=${secret_key}`
    )
      .then((response) => response.json())
      .then((data) => setActores([...data.cast.slice(0, 5)]));
  };

  return (
    
      <div>
        <MovieDetail {...movie} />
        <ActorList actores={actores} />
        <div class="container2"></div>
    <div class="container">
        <label class="titulo" for="">
            <h2>Wrath of Man</h2>

        </label>
        <p>
            A cold and mysterious new security guard for a Los Angeles cash truck company surprises his co-workers when
            he unleashes precision skills during a heist. The crew is left wondering who he is and where he came from.
            Soon, the marksman's ultimate motive becomes clear as he takes dramatic and irrevocable steps to settle a
            score.<br/>

            <label for=""> 2021-04-22</label>

             </p>
    </div>
    <div class="btn-close">x</div>
      </div>
    
  );
};

export default Detalle;
